class Articulo extends CI_Controller{
    private $session_data = "";
    private $sistema;
    function __construct()
    {
        parent::__construct();
        $this->load->model('Articulo_model');
        if ($this->session->userdata('logged_in')) {
            $this->session_data = $this->session->userdata('logged_in');
        }else {
            redirect('', 'refresh');
        }
        $this->load->model('Sistema_model');
        $this->sistema = $this->Sistema_model->get_sistema();
    }
    /* *****Funcion que verifica el acceso al sistema**** */
    private function acceso($id_rol){
        $data['sistema'] = $this->sistema;
        $rolusuario = $this->session_data['rol'];
        if($rolusuario[$id_rol-1]['rolusuario_asignado'] == 1){
            return true;
        }else{
            $data['_view'] = 'login/mensajeacceso';
            $this->load->view('layouts/main',$data);
        }
    }

    /*
     * Listing of articulo
     */
    function index()
    {
        $data['sistema'] = $this->sistema;
        if($this->acceso(155)){
            $data['page_title'] = "Articulo";
            $params['limit'] = RECORDS_PER_PAGE; 
            $params['offset'] = ($this->input->get('per_page')) ? $this->input->get('per_page') : 0;

            $config = $this->config->item('pagination');
            $config['base_url'] = site_url('articulo/index?');
            $config['total_rows'] = $this->Articulo_model->get_all_articulo_count();
            $this->pagination->initialize($config);

            $data['articulo'] = $this->Articulo_model->get_all_articulo($params);

            $data['_view'] = 'articulo/index';
            $this->load->view('layouts/main',$data);
        }
    }

    /*
     * Adding a new articulo
     */
    function add()
    {
        $data['sistema'] = $this->sistema;
        if($this->acceso(155)){
            $data['page_title'] = "Articulo";
            $this->load->library('form_validation');

                    $this->form_validation->set_rules('articulo_titulo','Articulo Titulo','required');

                    if($this->form_validation->run())     
            {   
                $params = array(
                                    'estadopag_id' => $this->input->post('estadopag_id'),
                                    'seccion_id' => $this->input->post('seccion_id'),
                                    'slide_id' => $this->input->post('slide_id'),
                                    'articulo_titulo' => $this->input->post('articulo_titulo'),
                                    'articulo_descripcion' => $this->input->post('articulo_descripcion'),
                                    'articulo_texto' => $this->input->post('articulo_texto'),
                );

                $articulo_id = $this->Articulo_model->add_articulo($params);
                redirect('articulo/index');
            }
            else
            {
                            $this->load->model('Estado_pagina_model');
                            $data['all_estado_pagina'] = $this->Estado_pagina_model->get_all_estado_pagina();

                            $this->load->model('Seccion_model');
                            $data['all_seccion'] = $this->Seccion_model->get_all_seccion();

                            $this->load->model('Slide_model');
                            $data['all_slide'] = $this->Slide_model->get_all_slide();

                $data['_view'] = 'articulo/add';
                $this->load->view('layouts/main',$data);
            }
        }
    }  

    /*
     * Editing a articulo
     */
    function edit($articulo_id)
    {
        $data['sistema'] = $this->sistema;
        if($this->acceso(155)){
            $data['page_title'] = "Articulo";
            // check if the articulo exists before trying to edit it
            $data['articulo'] = $this->Articulo_model->get_articulo($articulo_id);

            if(isset($data['articulo']['articulo_id']))
            {
                $this->load->library('form_validation');

                            $this->form_validation->set_rules('articulo_titulo','Articulo Titulo','required');

                            if($this->form_validation->run())     
                {   
                    $params = array(
                                            'estadopag_id' => $this->input->post('estadopag_id'),
                                            'seccion_id' => $this->input->post('seccion_id'),
                                            'slide_id' => $this->input->post('slide_id'),
                                            'articulo_titulo' => $this->input->post('articulo_titulo'),
                                            'articulo_descripcion' => $this->input->post('articulo_descripcion'),
                                            'articulo_texto' => $this->input->post('articulo_texto'),
                    );

                    $this->Articulo_model->update_articulo($articulo_id,$params);            
                    redirect('articulo/index');
                }
                else
                {
                                    $this->load->model('Estado_pagina_model');
                                    $data['all_estado_pagina'] = $this->Estado_pagina_model->get_all_estado_pagina();

                                    $this->load->model('Seccion_model');
                                    $data['all_seccion'] = $this->Seccion_model->get_all_seccion();

                                    $this->load->model('Slide_model');
                                    $data['all_slide'] = $this->Slide_model->get_all_slide();

                    $data['_view'] = 'articulo/edit';
                    $this->load->view('layouts/main',$data);
                }
            }
            else
                show_error('The articulo you are trying to edit does not exist.');
        }
    } 

    /*
     * Deleting articulo
     */
    function remove($articulo_id)
    {
        $data['sistema'] = $this->sistema;
        if($this->acceso(155)){
            $articulo = $this->Articulo_model->get_articulo($articulo_id);

            // check if the articulo exists before trying to delete it
            if(isset($articulo['articulo_id']))
            {
                $this->Articulo_model->delete_articulo($articulo_id);
                redirect('articulo/index');
            }
            else
                show_error('The articulo you are trying to delete does not exist.');
        }
    }
    
}
