    /*
     * Adding a new categoria_cliente
     */
    function add()
    {
        $data['sistema'] = $this->sistema;
        if($this->acceso(114)){
            $data['page_title'] = "Categoria Negocio Cliente";
            $this->load->library('form_validation');

