            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'promocion_id' => $this->input->post('promocion_id'),
					'detallepromo_cantidad' => $this->input->post('detallepromo_cantidad'),
					'detallepromo_precio' => $this->input->post('detallepromo_precio'),
					'producto_id' => $this->input->post('producto_id'),
                );

                $this->Detalle_promocion_model->update_detalle_promocion($detallepromo_id,$params);            
                redirect('detalle_promocion/index');
            }
            else
            {
				$this->load->model('Promocion_model');
				$data['all_promocion'] = $this->Promocion_model->get_all_promocion();

                $data['_view'] = 'detalle_promocion/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('The detalle_promocion you are trying to edit does not exist.');
    } 

    /*
     * Deleting detalle_promocion
     */
    function remove($detallepromo_id,$promocion_id)
    {
        $data['sistema'] = $this->sistema;
        $detalle_promocion = $this->Detalle_promocion_model->get_detalle_promocion($detallepromo_id);

        // check if the detalle_promocion exists before trying to delete it
        if(isset($detalle_promocion['detallepromo_id']))
        {
            $this->Detalle_promocion_model->delete_detalle_promocion($detallepromo_id);
