    /*
     * Editing a detalle_venta
     */
    function edit($detalleven_id)
    {   
        $data['sistema'] = $this->sistema;
        // check if the detalle_venta exists before trying to edit it
        $data['detalle_venta'] = $this->Detalle_venta_model->get_detalle_venta($detalleven_id);
        
        if(isset($data['detalle_venta']['detalleven_id']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'producto_id' => $this->input->post('producto_id'),
					'venta_id' => $this->input->post('venta_id'),
					'moneda_id' => $this->input->post('moneda_id'),
					'detalleven_codigo' => $this->input->post('detalleven_codigo'),
					'detalleven_cantidad' => $this->input->post('detalleven_cantidad'),
					'detalleven_unidad' => $this->input->post('detalleven_unidad'),
					'detalleven_costo' => $this->input->post('detalleven_costo'),
					'detalleven_precio' => $this->input->post('detalleven_precio'),
					'detalleven_subtotal' => $this->input->post('detalleven_subtotal'),
					'detalleven_descuento' => $this->input->post('detalleven_descuento'),
					'detalleven_total' => $this->input->post('detalleven_total'),
					'detalleven_preferencia' => $this->input->post('detalleven_preferencia'),
					'detalleven_comision' => $this->input->post('detalleven_comision'),
					'detalleven_tipocambio' => $this->input->post('detalleven_tipocambio'),
					'detalleven_caracteristicas' => $this->input->post('detalleven_caracteristicas'),
                );

                $this->Detalle_venta_model->update_detalle_venta($detalleven_id,$params);            
                redirect('detalle_venta/index');
            }
            else
            {
				$this->load->model('Producto_model');
				$data['all_producto'] = $this->Producto_model->get_all_producto();

				$this->load->model('Venta_model');
				$data['all_venta'] = $this->Venta_model->get_all_venta();

				$this->load->model('Moneda_model');
				$data['all_moneda'] = $this->Moneda_model->get_all_moneda();

                $data['_view'] = 'detalle_venta/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('The detalle_venta you are trying to edit does not exist.');
    } 

    /*
