    function remove($detalleven_id)
    {
        $data['sistema'] = $this->sistema;
        $detalle_venta = $this->Detalle_venta_model->get_detalle_venta($detalleven_id);

        // check if the detalle_venta exists before trying to delete it
        if(isset($detalle_venta['detalleven_id']))
        {
            $this->Detalle_venta_model->delete_detalle_venta($detalleven_id);
            redirect('detalle_venta/index');
        }
        else
            show_error('The detalle_venta you are trying to delete does not exist.');
    }
