class Documento_respaldo extends CI_Controller{
    
    private $sistema;
    function __construct()
    {
        parent::__construct();
        $this->load->model('Documento_respaldo_model');
        $this->load->model('Sistema_model');
        $this->sistema = $this->Sistema_model->get_sistema();
    } 

    /*
     * Listing of documento_respaldo
     */
    function index()
    {
        $data['sistema'] = $this->sistema;
        $data['documento_respaldo'] = $this->Documento_respaldo_model->get_all_documento_respaldo();
        
        $data['_view'] = 'documento_respaldo/index';
        $this->load->view('layouts/main',$data);
    }

    /*
     * Adding a new documento_respaldo
     */
    function add()
    {   
        $data['sistema'] = $this->sistema;
        if(isset($_POST) && count($_POST) > 0)     
        {   
            $params = array(
				'documento_respaldo_descripcion' => $this->input->post('documento_respaldo_descripcion'),
            );
            
            $documento_respaldo_id = $this->Documento_respaldo_model->add_documento_respaldo($params);
            redirect('documento_respaldo/index');
        }
        else
        {            
            $data['_view'] = 'documento_respaldo/add';
            $this->load->view('layouts/main',$data);
        }
    }  

    /*
     * Editing a documento_respaldo
     */
    function edit($documento_respaldo_id)
    {   
        $data['sistema'] = $this->sistema;
        // check if the documento_respaldo exists before trying to edit it
        $data['documento_respaldo'] = $this->Documento_respaldo_model->get_documento_respaldo($documento_respaldo_id);
        
        if(isset($data['documento_respaldo']['documento_respaldo_id']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'documento_respaldo_descripcion' => $this->input->post('documento_respaldo_descripcion'),
                );

                $this->Documento_respaldo_model->update_documento_respaldo($documento_respaldo_id,$params);            
                redirect('documento_respaldo/index');
            }
            else
            {
                $data['_view'] = 'documento_respaldo/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('The documento_respaldo you are trying to edit does not exist.');
    } 

    /*
     * Deleting documento_respaldo
     */
    function remove($documento_respaldo_id)
    {
        $data['sistema'] = $this->sistema;
        $documento_respaldo = $this->Documento_respaldo_model->get_documento_respaldo($documento_respaldo_id);

        // check if the documento_respaldo exists before trying to delete it
        if(isset($documento_respaldo['documento_respaldo_id']))
        {
            $this->Documento_respaldo_model->delete_documento_respaldo($documento_respaldo_id);
            redirect('documento_respaldo/index');
        }
        else
            show_error('The documento_respaldo you are trying to delete does not exist.');
    }
    
}
