                    'dosificacion_leyenda1' => $this->input->post('dosificacion_leyenda1'),
                    'dosificacion_leyenda2' => $this->input->post('dosificacion_leyenda2'),
                    'dosificacion_leyenda3' => $this->input->post('dosificacion_leyenda3'),
