class Estado_pagina extends CI_Controller{
    
    private $session_data = "";
    private $sistema;
    function __construct()
    {
        parent::__construct();
        $this->load->model('Estado_pagina_model');
        if ($this->session->userdata('logged_in')) {
            $this->session_data = $this->session->userdata('logged_in');
        }else {
            redirect('', 'refresh');
        }
        $this->load->model('Sistema_model');
        $this->sistema = $this->Sistema_model->get_sistema();
        $data['sistema'] = $this->sistema;
    } 
    /* *****Funcion que verifica el acceso al sistema**** */
    private function acceso($id_rol){
        
        $data['sistema'] = $this->sistema;
        $rolusuario = $this->session_data['rol'];
        if($rolusuario[$id_rol-1]['rolusuario_asignado'] == 1){
            return true;
        }else{
            $data['_view'] = 'login/mensajeacceso';
            $this->load->view('layouts/main',$data);
        }
    }
    /*
     * Listing of estado_pagina
     */
    function index()
    {
        $data['sistema'] = $this->sistema;
        if($this->acceso(155)){
            $data['page_title'] = "Estado Pagina";
            $params['limit'] = RECORDS_PER_PAGE; 
            $params['offset'] = ($this->input->get('per_page')) ? $this->input->get('per_page') : 0;

            $config = $this->config->item('pagination');
            $config['base_url'] = site_url('estado_pagina/index?');
            $config['total_rows'] = $this->Estado_pagina_model->get_all_estado_pagina_count();
            $this->pagination->initialize($config);

            $data['estado_pagina'] = $this->Estado_pagina_model->get_all_estado_pagina($params);

            $data['_view'] = 'estado_pagina/index';
            $this->load->view('layouts/main',$data);
        }
    }

    /*
     * Adding a new estado_pagina
     */
    function add()
    {
        $data['sistema'] = $this->sistema;
        if($this->acceso(155)){
            $data['page_title'] = "Estado Pagina";
            $this->load->library('form_validation');

                    $this->form_validation->set_rules('estadopag_descripcion','Estadopag Descripcion','required');

                    if($this->form_validation->run())     
            {   
                $params = array(
                                    'estadopag_descripcion' => $this->input->post('estadopag_descripcion'),
                );

                $estado_pagina_id = $this->Estado_pagina_model->add_estado_pagina($params);
                redirect('estado_pagina/index');
            }
            else
            {            
                $data['_view'] = 'estado_pagina/add';
                $this->load->view('layouts/main',$data);
            }
        }
    }  

    /*
     * Editing a estado_pagina
     */
    function edit($estadopag_id)
    {
        $data['sistema'] = $this->sistema;
        if($this->acceso(155)){
            $data['page_title'] = "Estado Pagina";
            // check if the estado_pagina exists before trying to edit it
            $data['estado_pagina'] = $this->Estado_pagina_model->get_estado_pagina($estadopag_id);

            if(isset($data['estado_pagina']['estadopag_id']))
            {
                $this->load->library('form_validation');

                            $this->form_validation->set_rules('estadopag_descripcion','Estadopag Descripcion','required');

                            if($this->form_validation->run())     
                {   
                    $params = array(
                                            'estadopag_descripcion' => $this->input->post('estadopag_descripcion'),
                    );

                    $this->Estado_pagina_model->update_estado_pagina($estadopag_id,$params);            
                    redirect('estado_pagina/index');
                }
                else
                {
                    $data['_view'] = 'estado_pagina/edit';
                    $this->load->view('layouts/main',$data);
                }
            }
            else
                show_error('The estado_pagina you are trying to edit does not exist.');
        }
    } 

    /*
     * Deleting estado_pagina
     */
    function remove($estadopag_id)
    {
        $data['sistema'] = $this->sistema;
        if($this->acceso(155)){
            $estado_pagina = $this->Estado_pagina_model->get_estado_pagina($estadopag_id);

            // check if the estado_pagina exists before trying to delete it
            if(isset($estado_pagina['estadopag_id']))
            {
                $this->Estado_pagina_model->delete_estado_pagina($estadopag_id);
                redirect('estado_pagina/index');
            }
            else
                show_error('The estado_pagina you are trying to delete does not exist.');
        }
    }
    
}
