    function imprimir_factura($venta_id)
    {
        if($this->acceso(17)){
        //**************** inicio contenido ***************            
                
            $parametros = $this->Parametro_model->get_parametros();

            if (sizeof($parametros)>0){
                
                if ($parametros[0]['parametro_tipoimpresora']=="FACTURADORA")
