    /*
     * Deleting gestion
     */
    function remove($cod_ges)
    {
        $data['sistema'] = $this->sistema;
        $gestion = $this->Gestion_model->get_gestion($cod_ges);

        // check if the gestion exists before trying to delete it
        if(isset($gestion['cod_ges']))
