class Inventario_sucursal extends CI_Controller{
    
    private $session_data = "";
    private $sistema;
    function __construct()
    {
        parent::__construct();
        $this->load->model('Inventario_sucursal_model');
        $this->load->model('Empresa_model');
        $this->load->model('Producto_model');
        if ($this->session->userdata('logged_in')) {
            $this->session_data = $this->session->userdata('logged_in');
        }else {
            redirect('', 'refresh');
        }
        $this->load->model('Sistema_model');
        $this->sistema = $this->Sistema_model->get_sistema();
    }
    private function acceso($id_rol){
        
        $data['sistema'] = $this->sistema;
        $rolusuario = $this->session_data['rol'];
        if($rolusuario[$id_rol-1]['rolusuario_asignado'] == 1){
            return true;
        }else{
            $data['_view'] = 'login/mensajeacceso';
            $this->load->view('layouts/main',$data);
        }
    } 

    /*
     * Listing of producto
     */
    function index()
    {
        $data['sistema'] = $this->sistema;
        if($this->acceso(24)){
            //**************** inicio contenido ***************
            $data['rolusuario'] = $this->session_data['rol'];
            $empresa_id = 1;
            $data['page_title'] = "Inventario";
            $data['empresa'] = $this->Empresa_model->get_empresa($empresa_id);
            
            $this->load->model('Parametro_model');
            $data['parametro'] = $this->Parametro_model->get_parametros();
            $this->load->model('Moneda_model');
            $data['moneda'] = $this->Moneda_model->get_moneda(2); //Obtener moneda extragera
            $data['lamoneda'] = $this->Moneda_model->getalls_monedasact_asc();
            
            $data['almacenes'] = $this->Inventario_model->get_almacenes();
            
            $data['_view'] = 'inventario_sucursal/index';
            $this->load->view('layouts/main',$data);

            //**************** fin contenido ***************
        }
			
    }

    /*
     * muestra los inventarios por sucursales
     */
    function mostrar_inventarios()
    {
        $data['sistema'] = $this->sistema;
        if($this->acceso(25)){
            $parametro = $this->input->post("parametro");
            //$select_almacen = $this->input->post("select_almacen");
            
            if ($parametro=="" || $parametro==null){
                $resultado = $this->Inventario_sucursal_model->get_inventario_sucursales();
            }else{
                $resultado = $this->Inventario_sucursal_model->get_inventario_sucursalesparametro($parametro);
            }
            echo json_encode($resultado);
        }
    }
    
}
