    /*
     * muestra los inventarios por sucursales
     */
    function mostrar_inventarios()
    {
        if($this->input->is_ajax_request()){
            
        //$data['sistema'] = $this->sistema;
        if($this->acceso(25)){
            $parametro = $this->input->post("parametro");
            //$select_almacen = $this->input->post("select_almacen");
            
            if ($parametro=="" || $parametro==null){
                $resultado = $this->Inventario_sucursal_model->get_inventario_sucursales();
            }else{
                $resultado = $this->Inventario_sucursal_model->get_inventario_sucursalesparametro($parametro);
            }
            echo json_encode($resultado);
        }
        }else{ show_404();}
    }
    
