class Mesa extends CI_Controller{
    
    private $sistema;
    private $parametros;
    
    function __construct()
    {
        parent::__construct();
        $this->load->model('Mesa_model');
        $this->load->model('Sistema_model');
        $this->load->model('Estado_model');
        $this->load->model('Venta_model');
        $this->sistema = $this->Sistema_model->get_sistema();
        
        $this->load->model('Parametro_model');
        $parametro = $this->Parametro_model->get_parametros();
        $this->parametros = $parametro[0];
        
        if ($this->session->userdata('logged_in')) {
            $this->session_data = $this->session->userdata('logged_in');
        }else {
            redirect('', 'refresh');
        }
    } 

    /*
     * Listing of mesa
     */
    function index()
    {
        $data['sistema'] = $this->sistema;
        $data['mesa'] = $this->Mesa_model->get_all_mesa();
        
        $data['_view'] = 'mesa/index';
        $this->load->view('layouts/main',$data);
    }

    /*
     * Adding a new mesa
     */
    function add()
    {  
        $data['sistema'] = $this->sistema;
        if(isset($_POST) && count($_POST) > 0)     
        {
            $params = array(
                'usuario_id' => $this->input->post('usuario_id'),
                'mesa_nombre' => $this->input->post('mesa_nombre'),
            );
            $mesa_id = $this->Mesa_model->add_mesa($params);
            redirect('mesa/index');
        }
        else
        {
            $this->load->model('Usuario_model');
            $data['all_usuario'] = $this->Usuario_model->get_all_usuario_activo();
            
            $data['_view'] = 'mesa/add';
            $this->load->view('layouts/main',$data);
        }
    }  

    /*
     * Editing a mesa
     */
    function edit($mesa_id)
    {
        $data['sistema'] = $this->sistema;
        // check if the mesa exists before trying to edit it
        $data['mesa'] = $this->Mesa_model->get_mesa($mesa_id);
        if(isset($data['mesa']['mesa_id']))
        {
            if(isset($_POST) && count($_POST) > 0)
            {
                $params = array(
                    'usuario_id' => $this->input->post('usuario_id'),
                    'mesa_nombre' => $this->input->post('mesa_nombre'),
                );
                $this->Mesa_model->update_mesa($mesa_id,$params);            
                redirect('mesa/index');
            }else{
                $this->load->model('Usuario_model');
                $data['all_usuario'] = $this->Usuario_model->get_all_usuario();

                $data['_view'] = 'mesa/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('The mesa you are trying to edit does not exist.');
    }

    /*
     * Deleting mesa
     */
    function remove($mesa_id)
    {
        $data['sistema'] = $this->sistema;
        $mesa = $this->Mesa_model->get_mesa($mesa_id);

        // check if the mesa exists before trying to delete it
        if(isset($mesa['mesa_id']))
        {
            $this->Mesa_model->delete_mesa($mesa_id);
            redirect('mesa/index');
        }
        else
            show_error('The mesa you are trying to delete does not exist.');
    }
    
    
     /*
     * Vista para mostrar mesas
     */
    function mesas()
    {
        $data['usuario_id'] = $this->session_data['usuario_id'];
        $data['sistema'] = $this->sistema;
        $data['mesa'] = $this->Mesa_model->get_all_mesa();
        $data['categorias'] = $this->Mesa_model->get_all_categorias();
        $data['all_estado'] = $this->Estado_model->get_tipo_estado(10); //estados basicos de tipo 1
        $data['parametros'] = $this->parametros;
        
        $data['_view'] = 'mesa/mesas';
        $this->load->view('layouts/main',$data);
    }
    
    /*
    * Verficar usuario
    */
    function cambiar_estado(){

        if ($this->input->is_ajax_request()) {
            
            $estado_id = $this->input->post("estado_id"); 
            $mesa_id = $this->input->post("mesa_id"); 
            
            $sql = "update mesa set estado_id = {$estado_id} where mesa_id = {$mesa_id} ";
            $resultado = $this->Venta_model->ejecutar($sql);            
            
            echo json_encode(true);                

            

        }


    }
    
}
