class Mesa extends CI_Controller{
    
    private $sistema;
    private $parametros;
    
    function __construct()
    {
        parent::__construct();
        $this->load->model('Mesa_model');
        $this->load->model('Sistema_model');
        $this->load->model('Estado_model');
        $this->load->model('Venta_model');
        $this->sistema = $this->Sistema_model->get_sistema();
        
        $this->load->model('Parametro_model');
        $parametro = $this->Parametro_model->get_parametros();
        $this->parametros = $parametro[0];
        
        if ($this->session->userdata('logged_in')) {
            $this->session_data = $this->session->userdata('logged_in');
        }else {
            redirect('', 'refresh');
        }
    } 

    /*
     * Listing of mesa
     */
    function index()
    {
        $data['sistema'] = $this->sistema;
        $data['mesa'] = $this->Mesa_model->get_all_mesa();
        
        $data['_view'] = 'mesa/index';
        $this->load->view('layouts/main',$data);
    }

    /*
     * Adding a new mesa
     */
    function add()
    {  
        $data['sistema'] = $this->sistema;
        if(isset($_POST) && count($_POST) > 0)     
        {
            $params = array(
                'usuario_id' => $this->input->post('usuario_id'),
                'mesa_nombre' => $this->input->post('mesa_nombre'),
            );
            $mesa_id = $this->Mesa_model->add_mesa($params);
            redirect('mesa/index');
        }
        else
        {
            $this->load->model('Usuario_model');
            $data['all_usuario'] = $this->Usuario_model->get_all_usuario_activo();
            
            $data['_view'] = 'mesa/add';
            $this->load->view('layouts/main',$data);
        }
    }  

    /*
     * Editing a mesa
     */
    function edit($mesa_id)
    {
        $data['sistema'] = $this->sistema;
        // check if the mesa exists before trying to edit it
        $data['mesa'] = $this->Mesa_model->get_mesa($mesa_id);
        if(isset($data['mesa']['mesa_id']))
        {
            if(isset($_POST) && count($_POST) > 0)
            {
                $params = array(
                    'usuario_id' => $this->input->post('usuario_id'),
                    'mesa_nombre' => $this->input->post('mesa_nombre'),
                );
                $this->Mesa_model->update_mesa($mesa_id,$params);            
                redirect('mesa/index');
            }else{
                $this->load->model('Usuario_model');
                $data['all_usuario'] = $this->Usuario_model->get_all_usuario();

                $data['_view'] = 'mesa/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('The mesa you are trying to edit does not exist.');
    }

    /*
     * Deleting mesa
     */
    function remove($mesa_id)
    {
        $data['sistema'] = $this->sistema;
        $mesa = $this->Mesa_model->get_mesa($mesa_id);

        // check if the mesa exists before trying to delete it
        if(isset($mesa['mesa_id']))
        {
            $this->Mesa_model->delete_mesa($mesa_id);
            redirect('mesa/index');
        }
        else
            show_error('The mesa you are trying to delete does not exist.');
    }
    
    
     /*
     * Vista para mostrar mesas
     */
    function mesas()
    {
        $data['usuario_id'] = $this->session_data['usuario_id'];
        $data['sistema'] = $this->sistema;
        $data['mesa'] = $this->Mesa_model->get_all_mesa();
        $data['categorias'] = $this->Mesa_model->get_all_categorias();
        $data['all_estado'] = $this->Estado_model->get_tipo_estado(10); //estados basicos de tipo 1
        $data['parametros'] = $this->parametros;
        
        $data['_view'] = 'mesa/mesas';
        $this->load->view('layouts/main',$data);
    }
    
    /*
    * Verficar usuario
    */
    function cambiar_estado(){

        if ($this->input->is_ajax_request()) {
            
            $estado_id = $this->input->post("estado_id"); 
            $mesa_id = $this->input->post("mesa_id"); 
            
            $sql = "update mesa set estado_id = {$estado_id} where mesa_id = {$mesa_id} ";
            $resultado = $this->Venta_model->ejecutar($sql);            
            
            echo json_encode(true);                

            

        }


    }

    /*
    * ocupar_mesa
    */
    function ocupar_mesa(){

        if ($this->input->is_ajax_request()) {
            
            $estado_id = $this->input->post("estado_id"); 
            
            $now = "'".date("Y-m-d H:i:s")."'"; //{$now}
            $usuario_id = $this->session_data['usuario_id'];;
            $estado_id = 11; //Pendiente
            $cliente_id = 1;
            $tipotrans_id = 1;
            $pedido_fecha = $now;
            $pedido_subtotal = 0;
            $pedido_descuento = 0;
            $pedido_total = 0;
            $pedido_glosa = "";
            //$pedido_fechaentrega = ;
            //$pedido_horaentrega = ;
            $pedido_latitud = 0;
            $pedido_longitud = 0; 
            $regusuario_id = $usuario_id; 
            $mesa_id = $this->input->post("mesa_id"); 
            
            
            //Registramos el pedido/comanda
            $sql = "insert into pedido(usuario_id, estado_id, cliente_id, tipotrans_id, pedido_fecha, 
            pedido_subtotal, pedido_descuento, pedido_total, pedido_glosa, 
            pedido_fechaentrega, pedido_horaentrega, pedido_latitud, 
            pedido_longitud, regusuario_id, mesa_id) value({$usuario_id}, {$estado_id}, {$cliente_id}, {$tipotrans_id}, {$pedido_fecha},
            {$pedido_subtotal}, {$pedido_descuento}, {$pedido_total}, '{$pedido_glosa}', date({$now}),time({$now}), {$pedido_latitud},
            {$pedido_longitud}, {$regusuario_id}, {$mesa_id})";            
            $pedido_id = $this->Venta_model->ejecutar($sql);
            
            //cambiamos de estado la mesas
            $estado_id = 39; //Pendiente
            $sql = "update mesa set estado_id = {$estado_id} where mesa_id = {$mesa_id} ";
            $this->Venta_model->ejecutar($sql);
            
            //
//            $sql = "select * pedido where pedido_id = {$pedido_id} ";
//            $resultado = $this->Venta_model->consultar($sql);
                        
            echo json_encode($pedido_id);                

            

        }


    }
    
    /*
    * mostra detalle mesa
    */
    function mostrar_datos_pedido(){

        if ($this->input->is_ajax_request()) {
            
            $pedido_id = $this->input->post("pedido_id"); 
            
            $sql = "select p.*, e.estado_descripcion, e.estado_color, c.*, m.*
                    from pedido p
                    left join estado e on e.estado_id = p.estado_id
                    left join cliente c on c.cliente_id = p.cliente_id
                    left join mesa m on m.mesa_id = p.mesa_id
                    where p.pedido_id = {$pedido_id}";
            $resultado = $this->Venta_model->consultar($sql);
                        
            echo json_encode($resultado);      

            

        }


    }
    
    /*
    * mostra detalle mesa
    */
    function get_pedido_asociado(){

        if ($this->input->is_ajax_request()) {
            
            $mesa_id = $this->input->post("mesa_id"); 
            
            $sql = "select pedido_id from pedido where estado_id = 11 and mesa_id = {$mesa_id} ";
            $resultado = $this->Venta_model->consultar($sql);
                        
            if (is_array($resultado)){
                echo json_encode($resultado[0]["pedido_id"]);
            }else{
                echo json_encode(0);
            }

            

        }


    }
    
    /*
