        $sql = "insert into detalle_venta_aux(
            producto_id,
            venta_id,
            detalleven_codigo,
            detalleven_cantidad,
            detalleven_unidad,
            detalleven_costo,
            detalleven_precio,
            detalleven_subtotal,
            detalleven_descuento,
            detalleven_total,
            detalleven_caracteristicas,
            detalleven_preferencia,
            detalleven_comision,
            detalleven_tipocambio,
            usuario_id)
            
            (select 
            d.producto_id,
            0 as venta_id,
            d.detalleped_codigo,
            d.detalleped_cantidad,
            d.detalleped_unidad,
            d.detalleped_costo,
