class Procedencia extends CI_Controller{
    private $sistema;
    function __construct()
    {
        parent::__construct();
        $this->load->model('Procedencia_model');
        if ($this->session->userdata('logged_in')) {
            $this->session_data = $this->session->userdata('logged_in');
        }else {
            redirect('', 'refresh');
        }
        $this->load->model('Sistema_model');
        $this->sistema = $this->Sistema_model->get_sistema();
    } 
private function acceso($id_rol){
    
        $data['sistema'] = $this->sistema;
        $rolusuario = $this->session_data['rol'];
        if($rolusuario[$id_rol-1]['rolusuario_asignado'] == 1){
            return true;
        }else{
            $data['_view'] = 'login/mensajeacceso';
            $this->load->view('layouts/main',$data);
        }
    }
    /*
     * Listing of procedencia
     */
    function index()
    {
        $data['sistema'] = $this->sistema;
        if($this->acceso(126)) {
        $data['procedencia'] = $this->Procedencia_model->get_all_procedencia();
        $data['page_title'] = "Procedencia";
        $data['_view'] = 'procedencia/index';
        $this->load->view('layouts/main',$data);
        }
            
    }

    /*
     * Adding a new procedencia
     */
    function add()
    {
        $data['sistema'] = $this->sistema;
        if($this->acceso(126)) {
        if(isset($_POST) && count($_POST) > 0)     
        {   
            //se inicia en ACTIVO
            $estado_id = 1;
            $params = array(
				'procedencia_descripcion' => $this->input->post('procedencia_descripcion'),
				'estado_id' => $estado_id,
            );
            
            $procedencia_id = $this->Procedencia_model->add_procedencia($params);
            redirect('procedencia/index');
        }
        else
        {
            $data['page_title'] = "Procedencia";
            $data['_view'] = 'procedencia/add';
            $this->load->view('layouts/main',$data);
        }
        }
            
    }

    /*
     * Editing a procedencia
     */
    function edit($procedencia_id)
    {
        $data['sistema'] = $this->sistema;
        if($this->acceso(126)) {
        // check if the procedencia exists before trying to edit it
        $data['procedencia'] = $this->Procedencia_model->get_procedencia($procedencia_id);
        
        if(isset($data['procedencia']['procedencia_id']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'procedencia_descripcion' => $this->input->post('procedencia_descripcion'),
					'estado_id' => $this->input->post('estado_id'),
                );

                $this->Procedencia_model->update_procedencia($procedencia_id,$params);            
                redirect('procedencia/index');
            }
            else
            {
                $this->load->model('Estado_model');
	        $data['all_estado'] = $this->Estado_model->get_all_estado_activo_inactivo();
            $data['page_title'] = "Procedencia";
                $data['_view'] = 'procedencia/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('La procedencia que estas intentando editar, no existe.');
        }
           
    } 

    /*
     * Deleting procedencia
     */
    function remove($procedencia_id)
    {
        $data['sistema'] = $this->sistema;
        if($this->acceso(126)) {
        $procedencia = $this->Procedencia_model->get_procedencia($procedencia_id);

        // check if the procedencia exists before trying to delete it
        if(isset($procedencia['procedencia_id']))
        {
            $this->Procedencia_model->delete_procedencia($procedencia_id);
            redirect('procedencia/index');
        }
        else
            show_error('La procedencia que quiere eliminar, no existe.');
        }
            
    }
    
}
