class Proyecto extends CI_Controller{
    
    private $sistema;
    function __construct()
    {
        parent::__construct();
        $this->load->model('Proyecto_model');
        $this->load->model('Sistema_model');
    } 

    /*
     * Listing of proyecto
     */
    function index()
    {
        $data['proyecto'] = $this->Proyecto_model->get_all_proyecto();
        
        $data['_view'] = 'proyecto/index';
        $this->load->view('layouts/main',$data);
        $this->sistema = $this->Sistema_model->get_sistema();
    }

    /*
     * Adding a new proyecto
     */
    function add()
    {   
        $data['sistema'] = $this->sistema;
        if(isset($_POST) && count($_POST) > 0)     
        {   
            $params = array(
				'numero_proyecto' => $this->input->post('numero_proyecto'),
				'nombre_proyecto' => $this->input->post('nombre_proyecto'),
            );
            
            $proyecto_id = $this->Proyecto_model->add_proyecto($params);
            redirect('proyecto/index');
        }
        else
        {            
            $data['_view'] = 'proyecto/add';
            $this->load->view('layouts/main',$data);
        }
    }  

    /*
     * Editing a proyecto
     */
    function edit($cod_proy)
    {   
        $data['sistema'] = $this->sistema;
        // check if the proyecto exists before trying to edit it
        $data['proyecto'] = $this->Proyecto_model->get_proyecto($cod_proy);
        
        if(isset($data['proyecto']['cod_proy']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'numero_proyecto' => $this->input->post('numero_proyecto'),
					'nombre_proyecto' => $this->input->post('nombre_proyecto'),
                );

                $this->Proyecto_model->update_proyecto($cod_proy,$params);            
                redirect('proyecto/index');
            }
            else
            {
                $data['_view'] = 'proyecto/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('The proyecto you are trying to edit does not exist.');
    } 

    /*
     * Deleting proyecto
     */
    function remove($cod_proy)
    {
        $data['sistema'] = $this->sistema;
        $proyecto = $this->Proyecto_model->get_proyecto($cod_proy);

        // check if the proyecto exists before trying to delete it
        if(isset($proyecto['cod_proy']))
        {
            $this->Proyecto_model->delete_proyecto($cod_proy);
            redirect('proyecto/index');
        }
        else
            show_error('The proyecto you are trying to delete does not exist.');
    }
    
}
