class Red_social extends CI_Controller{
    private $session_data = "";
    private $sistema;

    function __construct()
    {
        parent::__construct();
        $this->load->model('Red_social_model');
        if ($this->session->userdata('logged_in')) {
            $this->session_data = $this->session->userdata('logged_in');
        }else {
            redirect('', 'refresh');
        }
        $this->load->model('Sistema_model');
        $this->sistema = $this->Sistema_model->get_sistema();
        
    }
    /* *****Funcion que verifica el acceso al sistema**** */
    private function acceso($id_rol){
        
        $data['sistema'] = $this->sistema;
        $rolusuario = $this->session_data['rol'];
        if($rolusuario[$id_rol-1]['rolusuario_asignado'] == 1){
            return true;
        }else{
            $data['_view'] = 'login/mensajeacceso';
            $this->load->view('layouts/main',$data);
        }
    }
    /*
     * Listing of red_social
     */
    function index()
    {
        $data['sistema'] = $this->sistema;
        if($this->acceso(114)){
            $data['page_title'] = "Redes Sociales";
            
            $data['red_social'] = $this->Red_social_model->get_all_red_social();

            $data['_view'] = 'red_social/index';
            $this->load->view('layouts/main',$data);
        }
    }

    /*
     * Adding a new red_social
     */
    function add()
    {   
        $data['sistema'] = $this->sistema;
        if($this->acceso(114)){
            $data['page_title'] = "Nueva red social";
            $this->load->library('form_validation');
            
            $this->form_validation->set_rules('redsocial_nombre','Nombre es requerida','trim|required', array('required' => 'Este Campo no debe ser vacio'));
            if($this->form_validation->run())     
            {
                /* *********************INICIO imagen***************************** */
                $foto="";
                if (!empty($_FILES['redsocial_imagen']['name'])){
                    $this->load->library('image_lib');
                    $config['upload_path'] = './resources/web/images/redsocial/';
                    $img_full_path = $config['upload_path'];

                    $config['allowed_types'] = 'gif|jpeg|jpg|png';
                    $config['max_size'] = 0;
                    $config['max_width'] = 0;
                    $config['max_height'] = 0;

                    $new_name = time(); //str_replace(" ", "_", $this->input->post('proveedor_nombre'));
                    $config['file_name'] = $new_name; //.$extencion;
                    $config['file_ext_tolower'] = TRUE;

                    $this->load->library('upload', $config);
                    $this->upload->do_upload('redsocial_imagen');

                    $img_data = $this->upload->data();
                    $extension = $img_data['file_ext'];
                    /* ********************INICIO para resize***************************** */
                    if ($img_data['file_ext'] == ".jpg" || $img_data['file_ext'] == ".png" || $img_data['file_ext'] == ".jpeg" || $img_data['file_ext'] == ".gif") {
                        $conf['image_library'] = 'gd2';
                        $conf['source_image'] = $img_data['full_path'];
                        $conf['new_image'] = './resources/web/images/redsocial/';
                        $conf['maintain_ratio'] = TRUE;
                        $conf['create_thumb'] = FALSE;
                        $conf['width'] = 250;
                        $conf['height'] = 250;
                        $this->image_lib->clear();
                        $this->image_lib->initialize($conf);
                        if(!$this->image_lib->resize()){
                            echo $this->image_lib->display_errors('','');
                        }
                    }
                    /* ********************F I N  para resize***************************** */
                    $confi['image_library'] = 'gd2';
                    $confi['source_image'] = './resources/web/images/redsocial/'.$new_name.$extension;
                    $confi['new_image'] = './resources/web/images/redsocial/'."thumb_".$new_name.$extension;
                    $confi['create_thumb'] = FALSE;
                    $confi['maintain_ratio'] = TRUE;
                    $confi['width'] = 100;
                    $confi['height'] = 100;

                    $this->image_lib->clear();
                    $this->image_lib->initialize($confi);
                    $this->image_lib->resize();

                    $foto = $new_name.$extension;
                }
                /* *********************FIN imagen***************************** */
                $estado_id = 1;
                $params = array(
                    'estado_id' => $estado_id,
                    'redsocial_nombre' => $this->input->post('redsocial_nombre'),
                    'redsocial_direccion' => $this->input->post('redsocial_direccion'),
                    'redsocial_imagen' => $foto,
                    'redsocial_icono' => $this->input->post('redsocial_icono'),
                );
                $redsocial_id = $this->Red_social_model->add_red_social($params);
                redirect('red_social/index');
            }
            else
            {
                $data['_view'] = 'red_social/add';
                $this->load->view('layouts/main',$data);
            }
        }
    }  

    /*
     * Editing a red_social
     */
    function edit($redsocial_id)
    {   
        $data['sistema'] = $this->sistema;
        if($this->acceso(114)){
            $data['page_title'] = "Editar red social";
            // check if the red_social exists before trying to edit it
            $data['red_social'] = $this->Red_social_model->get_red_social($redsocial_id);

            if(isset($data['red_social']['redsocial_id']))
            {
                $this->load->library('form_validation');
                $this->form_validation->set_rules('redsocial_nombre','Nombre es requerida','trim|required', array('required' => 'Este Campo no debe ser vacio'));
                if($this->form_validation->run())     
                {
                    /* *********************INICIO imagen***************************** */
                    $foto="";
                    $foto1= $this->input->post('redsocial_imagen1');
                    if (!empty($_FILES['redsocial_imagen']['name']))
                    {
                        $config['upload_path'] = './resources/web/images/redsocial/';
                        $config['allowed_types'] = 'gif|jpeg|jpg|png';
                        $config['max_size'] = 0;
                        $config['max_width'] = 0;
                        $config['max_height'] = 0;

                        $new_name = time(); //str_replace(" ", "_", $this->input->post('proveedor_nombre'));
                        $config['file_name'] = $new_name; //.$extencion;
                        $config['file_ext_tolower'] = TRUE;

                        $this->load->library('image_lib');
                        $this->image_lib->initialize($config);

                        $this->load->library('upload', $config);
                        $this->upload->do_upload('redsocial_imagen');

                        $img_data = $this->upload->data();
                        $extension = $img_data['file_ext'];
                        /* ********************INICIO para resize***************************** */
                        if($img_data['file_ext'] == ".jpg" || $img_data['file_ext'] == ".png" || $img_data['file_ext'] == ".jpeg" || $img_data['file_ext'] == ".gif") {
                            $conf['image_library'] = 'gd2';
                            $conf['source_image'] = $img_data['full_path'];
                            $conf['new_image'] = './resources/web/images/redsocial/';
                            $conf['maintain_ratio'] = TRUE;
                            $conf['create_thumb'] = FALSE;
                            $conf['width'] = 250;
                            $conf['height'] = 250;

                            $this->image_lib->initialize($conf);
                            if(!$this->image_lib->resize()){
                                echo $this->image_lib->display_errors('','');
                            }
                            $this->image_lib->clear();
                        }
                        /* ********************F I N  para resize***************************** */
                        //$directorio = base_url().'resources/imagenes/';
                        $base_url = explode('/', base_url());
                        //$directorio = FCPATH.'resources\images\clientes\\';
                        $directorio = $_SERVER['DOCUMENT_ROOT'].'/'.$base_url[3].'/resources/web/images/redsocial/';
                        //$directorio = $_SERVER['DOCUMENT_ROOT'].'/ximpleman_web/resources/images/clientes/';
                        if(isset($foto1) && !empty($foto1)){
                            if(file_exists($directorio.$foto1)){
                                unlink($directorio.$foto1);
                                //$mimagenthumb = str_replace(".", "_thumb.", $foto1);
                                $mimagenthumb = "thumb_".$foto1;
                                if(file_exists($directorio.$mimagenthumb)){
                                    unlink($directorio.$mimagenthumb);
                                }
                            }
                        }
                        $confi['image_library'] = 'gd2';
                        $confi['source_image'] = './resources/web/images/redsocial/'.$new_name.$extension;
                        $confi['new_image'] = './resources/web/images/redsocial/'."thumb_".$new_name.$extension;
                        $confi['create_thumb'] = FALSE;
                        $confi['maintain_ratio'] = TRUE;
                        $confi['width'] = 100;
                        $confi['height'] = 100;

                        $this->image_lib->clear();
                        $this->image_lib->initialize($confi);
                        $this->image_lib->resize();

                        $foto = $new_name.$extension;
                    }else{
                        $foto = $foto1;
                    }
                    /* *********************FIN imagen***************************** */
                    $params = array(
                        'estado_id' => $this->input->post('estado_id'),
                        'redsocial_nombre' => $this->input->post('redsocial_nombre'),
                        'redsocial_direccion' => $this->input->post('redsocial_direccion'),
                        'redsocial_imagen' => $foto,
                        'redsocial_icono' => $this->input->post('redsocial_icono'),
                    );
                    $this->Red_social_model->update_red_social($redsocial_id,$params);            
                    redirect('red_social/index');
                }
                else
                {
                    $this->load->model('Estado_model');
                    $data['all_estado'] = $this->Estado_model->get_all_estado_activo_inactivo();
                    
                    $data['_view'] = 'red_social/edit';
                    $this->load->view('layouts/main',$data);
                }
            }
            else
                show_error('The red_social you are trying to edit does not exist.');
        }
    } 

