class Responsable extends CI_Controller{
    
    private $sistema;

    function __construct()
    {
        parent::__construct();
        $this->load->model('Responsable_model');
        $this->load->model('Sistema_model');
        $this->sistema = $this->Sistema_model->get_sistema();

    } 

    /*
     * Listing of responsable
     */
    function index()
    {
        $data['sistema'] = $this->sistema;
        $params['limit'] = RECORDS_PER_PAGE; 
        $params['offset'] = ($this->input->get('per_page')) ? $this->input->get('per_page') : 0;
        
        $config = $this->config->item('pagination');
        $config['base_url'] = site_url('responsable/index?');
        $config['total_rows'] = $this->Responsable_model->get_all_responsable_count();
        $this->pagination->initialize($config);

        $data['responsable'] = $this->Responsable_model->get_all_responsable($params);
        
        $data['_view'] = 'responsable/index';
        $this->load->view('layouts/main',$data);
    }

