                    );
                    $this->Servicio_model->update_servicio($servicio_id,$sumparams);
                    /* **********Cambia el estado del servicio segun el estado de los detalles*********** */
                    $res_ids = $this->Detalle_serv_model->get_ids_estado_detalle_serv($servicio_id);
