                        if($ids['estado_id'] == $estado_id){
                            $cont++;
                        }
                    }
                    if($cont == count($res_ids)){
                        $params = array(
                                    'estado_id' => $estado_id,
                        );
                        
                        $this->Servicio_model->update_servicio($servicio_id,$params);
                    }
                    
                    echo json_encode("ok");
