class Bancariz_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get bancariz by cod_banca
     */
    function get_bancariz($cod_banca)
    {
        $bancariz = $this->db->query("
            SELECT
                *

            FROM
                `bancariz`

            WHERE
                `cod_banca` = ?
        ",array($cod_banca))->row_array();

        return $bancariz;
    }
        
    /*
     * Get all bancariz
     */
    function get_all_bancariz()
    {
        $bancariz = $this->db->query("
            SELECT
                *

            FROM
                `bancariz`

            WHERE
                1 = 1

            ORDER BY `cod_banca` DESC
        ")->result_array();

        return $bancariz;
    }
        
    /*
     * function to add new bancariz
     */
    function add_bancariz($params)
    {
        $this->db->insert('bancariz',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update bancariz
     */
    function update_bancariz($cod_banca,$params)
    {
        $this->db->where('cod_banca',$cod_banca);
        return $this->db->update('bancariz',$params);
    }
    
    /*
     * function to delete bancariz
     */
    function delete_bancariz($cod_banca)
    {
        return $this->db->delete('bancariz',array('cod_banca'=>$cod_banca));
    }
}
