 */
 
class Boton_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get boton by boton_id
     */
    function get_boton($boton_id)
    {
        $boton = $this->db->query("
            SELECT
                *

            FROM
                `boton`

            WHERE
                `boton_id` = ?
        ",array($boton_id))->row_array();

        return $boton;
    }
    
    /*
     * Get all boton count
     */
    function get_all_boton_count()
    {
        $boton = $this->db->query("
            SELECT
                count(*) as count

            FROM
                `boton`
        ")->row_array();

        return $boton['count'];
    }
        
    /*
     * Get all boton
     */
    function get_all_boton($params = array())
    {
        $limit_condition = "";
        if(isset($params) && !empty($params))
            $limit_condition = " LIMIT " . $params['offset'] . "," . $params['limit'];
        
        $boton = $this->db->query("
            SELECT
                *

            FROM
                boton b, estado_pagina e

            WHERE
                b.estadopag_id = e.estadopag_id

            ORDER BY `boton_id` DESC

            " . $limit_condition . "
        ")->result_array();

        return $boton;
    }
        
    /*
     * function to add new boton
     */
    function add_boton($params)
    {
        $this->db->insert('boton',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update boton
     */
    function update_boton($boton_id,$params)
    {
        $this->db->where('boton_id',$boton_id);
        return $this->db->update('boton',$params);
    }
    
    /*
     * function to delete boton
     */
    function delete_boton($boton_id)
    {
        return $this->db->delete('boton',array('boton_id'=>$boton_id));
    }
}
