class Boton_slide_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get boton_slide by botonslide_id
     */
    function get_boton_slide($botonslide_id)
    {
        $boton_slide = $this->db->query("
            SELECT
                *

            FROM
                `boton_slide`

            WHERE
                `botonslide_id` = ?
        ",array($botonslide_id))->row_array();

        return $boton_slide;
    }
    
    /*
     * Get all boton_slide count
     */
    function get_all_boton_slide_count()
    {
        $boton_slide = $this->db->query("
            SELECT
                count(*) as count

            FROM
                `boton_slide`
        ")->row_array();

        return $boton_slide['count'];
    }
        
    /*
     * Get all boton_slide
     */
    function get_all_boton_slide($params = array())
    {
        $limit_condition = "";
        if(isset($params) && !empty($params))
            $limit_condition = " LIMIT " . $params['offset'] . "," . $params['limit'];
        
        $boton_slide = $this->db->query("
            SELECT
                *

            FROM
                boton_slide bs, slide s, boton b

            WHERE
                bs.slide_id = s.slide_id
                and bs.boton_id = b.boton_id

            ORDER BY `botonslide_id` DESC

            " . $limit_condition . "
        ")->result_array();

        return $boton_slide;
    }
        
    /*
     * function to add new boton_slide
     */
    function add_boton_slide($params)
    {
        $this->db->insert('boton_slide',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update boton_slide
     */
    function update_boton_slide($botonslide_id,$params)
    {
        $this->db->where('botonslide_id',$botonslide_id);
        return $this->db->update('boton_slide',$params);
    }
    
    /*
     * function to delete boton_slide
     */
    function delete_boton_slide($botonslide_id)
    {
        return $this->db->delete('boton_slide',array('botonslide_id'=>$botonslide_id));
    }
