    /*
     * Get caja by caja_id
     */
    function get_caja($caja_id)
    {
                     
        $caja = $this->db->query("
            SELECT
                *

            FROM
                `caja`

            WHERE
                `caja_id` = ?
        ",array($caja_id))->row_array();

        return $caja;
    }

