class Caja_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get caja by caja_id
     */
    function get_caja($caja_id)
    {
        $caja = $this->db->query("
            SELECT
                *

            FROM
                `caja`

            WHERE
                `caja_id` = ?
        ",array($caja_id))->row_array();

        return $caja;
    }
        
    /*
     * Get all caja
     */
    function get_all_caja()
    {
        $caja = $this->db->query("
            SELECT
                c.*, m.moneda_descripcion, e.estado_descripcion, u.usuario_nombre
            FROM
                `caja` c
            LEFT JOIN moneda m on c.moneda_id = m.moneda_id
            LEFT JOIN estado e on c.estado_id = e.estado_id
            LEFT JOIN usuario u on c. usuario_id = u.usuario_id
            WHERE
                1 = 1
            ORDER BY c.`caja_id` DESC
        ")->result_array();

        return $caja;
    }
        
    /*
     * function to add new caja
     */
    function add_caja($params)
    {
        $this->db->insert('caja',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update caja
     */
    function update_caja($caja_id,$params)
    {
        $this->db->where('caja_id',$caja_id);
        return $this->db->update('caja',$params);
    }
    
    /*
     * function to delete caja
     */
    function delete_caja($caja_id)
    {
        return $this->db->delete('caja',array('caja_id'=>$caja_id));
    }
    /*
     * Get caja by usuario_id
     */
    function get_cajausuario($usuario_id)
    {
        $caja = $this->db->query("
            SELECT
                *
            FROM
                `caja`
            WHERE
                `usuario_id` = $usuario_id
            order by caja_id desc
        ")->row_array();
        return $caja;
    }
}
