class Categoria_clientezona_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get categoria_clientezona by categoriacliezona_id
     */
    function get_categoria_clientezona($categoriacliezona_id)
    {
        return $this->db->get_where('zona',array('zona_id'=>$categoriacliezona_id))->row_array();
    }
        
    /*
     * Get all categoria_clientezona
     */
    function get_all_categoria_clientezona()
    {
        $clientezona = $this->db->query("
            SELECT
                *

            FROM
                zona cc, estado e

            WHERE
                cc.estado_id = e.estado_id
                
                ORDER BY cc.zona_id DESC

        ")->result_array();

        return $clientezona;
    }
        
    /*
     * function to add new categoria_clientezona
     */
    function add_categoria_clientezona($params)
    {
        $this->db->insert('zona',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update categoria_clientezona
     */
    function update_categoria_clientezona($categoriacliezona_id,$params)
    {
        $this->db->where('zona_id',$categoriacliezona_id);
        return $this->db->update('zona',$params);
    }
    
    /*
     * function to delete categoria_clientezona
     */
    function delete_categoria_clientezona($categoriacliezona_id)
    {
        return $this->db->delete('zona',array('zona_id'=>$categoriacliezona_id));
    }
    
    /*
     * Get all categoria_clientezona con id ==1
     */
    function get_all_categoria_clientezona_id1()
    {
        $categoriacliezona = $this->db->query("
            SELECT
                cc.`zona_id`, cc.`zona_nombre`

            FROM
                zona cc, estado e

            WHERE
                cc.estado_id = e.estado_id
                and cc.estado_id = 1
                
        ")->result_array();
        return $categoriacliezona;
    }
    /*
     * Get all categoria_clientezona ASC
     */
    function get_all_categoria_clientezona_asc()
    {
        $clientezona = $this->db->query("
            SELECT
                *
            FROM
                zona cc, estado e

            WHERE
                cc.estado_id = e.estado_id
                
                ORDER BY cc.zona_id ASC

        ")->result_array();

        return $clientezona;
    }

    function get_cliente_zona($cliente_id)
    {
        $sql = "select z.* from zona z,cliente c where c.cliente_id = ".$cliente_id." and c.zona_id = z.zona_id";
        $zona = $this->db->query($sql)->result_array();
        return $zona;
        
    }
