class Config_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get config by cod_conf
     */
    function get_config($cod_conf)
    {
        $config = $this->db->query("
            SELECT
                *

            FROM
                `config`

            WHERE
                `cod_conf` = ?
        ",array($cod_conf))->row_array();

        return $config;
    }
        
    /*
     * Get all config
     */
    function get_all_config()
    {
        $config = $this->db->query("
            SELECT
                *

            FROM
                `config`

            WHERE
                1 = 1

            ORDER BY `cod_conf` DESC
        ")->result_array();

        return $config;
    }
        
    /*
     * function to add new config
     */
    function add_config($params)
    {
        $this->db->insert('config',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update config
     */
    function update_config($cod_conf,$params)
    {
        $this->db->where('cod_conf',$cod_conf);
        return $this->db->update('config',$params);
    }
    
    /*
     * function to delete config
     */
    function delete_config($cod_conf)
    {
        return $this->db->delete('config',array('cod_conf'=>$cod_conf));
    }
}
