class Detalle_pedido_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get detalle_pedido by detalleped_id
     */
    function get_detalle_pedido($detalleped_id)
    {
        $detalle_pedido = $this->db->query("
            SELECT
                *

            FROM
                `detalle_pedido`

            WHERE
                `detalleped_id` = ?
        ",array($detalleped_id))->row_array();

        return $detalle_pedido;
    }
    
    /*
     * Get all detalle_pedido count
     */
    function get_all_detalle_pedido_count()
    {
        $detalle_pedido = $this->db->query("
            SELECT
                count(*) as count

            FROM
                `detalle_pedido`
        ")->row_array();

        return $detalle_pedido['count'];
    }
        
    /*
     * Get all detalle_pedido
     */
    function get_all_detalle_pedido($params = array())
    {
        $limit_condition = "";
        if(isset($params) && !empty($params))
            $limit_condition = " LIMIT " . $params['offset'] . "," . $params['limit'];
        
        $detalle_pedido = $this->db->query("
            SELECT
                *

            FROM
                detalle_pedido d, pedido p, producto pr

            WHERE
                d.pedido_id = p.pedido_id
                and d.producto_id = pr.producto_id

            ORDER BY `detalleped_id` DESC

            " . $limit_condition . "
        ")->result_array();

        return $detalle_pedido;
    }
        
    /*
     * function to add new detalle_pedido
     */
    function add_detalle_pedido($params)
    {
        $this->db->insert('detalle_pedido',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update detalle_pedido
     */
    function update_detalle_pedido($detalleped_id,$params)
    {
        $this->db->where('detalleped_id',$detalleped_id);
        return $this->db->update('detalle_pedido',$params);
    }
    
    /*
     * function to delete detalle_pedido
     */
    function delete_detalle_pedido($detalleped_id)
    {
        return $this->db->delete('detalle_pedido',array('detalleped_id'=>$detalleped_id));
    }
