<?php
/* 
 * Generated by CRUDigniter v3.2 
 * www.crudigniter.com
 */
 
class Dia_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get dia by cod_dia
     */
    function get_dia($cod_dia)
    {
        $dia = $this->db->query("
            SELECT
                *

            FROM
                `dia`

            WHERE
                `cod_dia` = ?
        ",array($cod_dia))->row_array();

        return $dia;
    }
        
    /*
     * Get all dia
     */
    function get_all_dia()
    {
        $dia = $this->db->query("
            SELECT
                *
            FROM
                `dia`
            WHERE
                1 = 1
            ORDER BY `cod_dia` DESC
        ")->result_array();

        return $dia;
    }
        
    /*
     * function to add new dia
     */
    function add_dia($params)
    {
        $this->db->insert('dia',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update dia
     */
    function update_dia($cod_dia,$params)
    {
        $this->db->where('cod_dia',$cod_dia);
        return $this->db->update('dia',$params);
    }
    
    /*
     * function to delete dia
     */
    function delete_dia($cod_dia)
    {
        return $this->db->delete('dia',array('cod_dia'=>$cod_dia));
    }
}
