class Empresa_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get empresa by empresa_id
     */
    function  get_empresa($empresa_id)
    {
        $empresa = $this->db->query("
            SELECT
                *

            FROM
                `empresa`

            WHERE
                `empresa_id` = 
        ".$empresa_id)->result_array();

        return $empresa;
    }
    
    /*
     * Get all empresa count
     */
    function get_all_empresa_count()
    {
        $empresa = $this->db->query("
            SELECT
                count(*) as count

            FROM
                `empresa`
        ")->row_array();

        return $empresa['count'];
    }
        
    /*
     * Get all empresa
     */
    function get_all_empresa($params = array())
    {
        $limit_condition = "";
        if(isset($params) && !empty($params))
            $limit_condition = " LIMIT " . $params['offset'] . "," . $params['limit'];
        
        $empresa = $this->db->query("
            SELECT
                *

            FROM
                `empresa`

            WHERE
                1 = 1

            ORDER BY `empresa_id`

            " . $limit_condition . "
        ")->result_array();

        return $empresa;
    }
        
    /*
     * function to add new empresa
     */
    function add_empresa($params)
    {
        $this->db->insert('empresa',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update empresa
     */
    function update_empresa($empresa_id,$params)
    {
        $this->db->where('empresa_id',$empresa_id);
        return $this->db->update('empresa',$params);
    }
    
    /*
     * function to delete empresa
     */
    function delete_empresa($empresa_id)
    {
        return $this->db->delete('empresa',array('empresa_id'=>$empresa_id));
    }
    /* *****Devielve el resultado en un arreglo simple********* */
    function get_this_empresa($empresa_id)
    {
        $empresa = $this->db->query("
            SELECT
                *

            FROM
                `empresa`

            WHERE
                `empresa_id` = 
        ".$empresa_id)->row_array();

        return $empresa;
    }
    
}
