    /*
     * Get estado by estado_id
     */
    function get_estado($estado_id)
    {
        $estado = $this->db->query("
            SELECT
                *

            FROM
                `estado`

            WHERE
                `estado_id` = ?
        ",array($estado_id))->row_array();

        return $estado;
    }
    
    /*
     * Get all estado count
     */
    function get_all_estado_count()
    {
        $estado = $this->db->query("
            SELECT
                count(*) as count

            FROM
                `estado`
        ")->row_array();

        return $estado['count'];
    }
        
    /*
     * Get all estado
     */
    function get_all_estado()
    {
        $estado = $this->db->query("
            SELECT
                *

            FROM
                `estado`

            WHERE
                1 = 1

            ORDER BY `estado_id` DESC

        ")->result_array();

        return $estado;
    }
        
