class Factura_compra_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * function to add new factura_compra
     */
    function add_facturacompra($params)
    {
        $this->db->insert('factura_compra',$params);
        return $this->db->insert_id();
    }
    
    /*
     * Get factura by factura_id
     */
    function get_facturacompra($factura_id)
    {
        $factura = $this->db->query("
            SELECT
                *
            FROM
                `factura_compra`
            WHERE
                `factura_id` = ?
        ",array($factura_id))->row_array();

        return $factura;
    }
    
    /*
     * function to update factura
     */
    function update_facturacompra($factura_id,$params)
    {
        $this->db->where('factura_id',$factura_id);
        return $this->db->update('factura_compra',$params);
    }
    
    /*
     * Get all factura
     */
    function getall_facturacompra($desde, $hasta, $filtro)
    {
        $factura = $this->db->query("
            SELECT
                f.*
            FROM
                factura_compra f
                left join compra c on f.compra_id = c.compra_id
                left join estado e on f.estado_id = e.estado_id
            WHERE
                f.factura_fecha >= '".$desde."'
                and f.factura_fecha <= '".$hasta."'
                $filtro
            ORDER BY `factura_id` DESC
        ")->result_array();

        return $factura;
    }
    
}
