class Factura_datos_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get factura_datos by datos_id
     */
    function get_factura_datos($datos_id)
    {
        $sql ="select * from factura_datos d, cliente c where d.datos_id = {$datos_id}  and c.cliente_id = d.cliente_id";
        
        $factura_datos = $this->db->query($sql)->row_array();

        return $factura_datos;
    }
    
    /*
     * Get all factura_datos
     */
    function get_all_factura_datos()
    {
        $factura_datos = $this->db->query("
            SELECT
                *

            FROM
                factura_datos f, estado e, venta v

            WHERE
                f.estado_id = e.estado_id
                and f.venta_id = v.venta_id

            ORDER BY `datos_id` DESC "
        )->result_array();

        return $factura_datos;
    }
        
    /*
     * function to add new factura_datos
     */
    function add_factura_datos($params)
    {
        $this->db->insert('factura_datos',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update factura_datos
     */
    function update_factura_datos($datos_id,$params)
    {
        $this->db->where('datos_id',$datos_id);
        return $this->db->update('factura_datos',$params);
    }
