    function get_factura_ventas($inicio, $fin)
    {
        $sql = "
            SELECT
                *

            FROM
                `factura`

            WHERE
                factura_fechaventa >= '".$inicio."'
                and factura_fechaventa <= '".$fin."'
                

            ORDER BY `factura_id` ASC";
        
        $factura = $this->db->query($sql)->result_array();
       
        return $factura;
    }

    function get_factura_compras($inicio, $fin)
    {
