                *

            FROM
                `factura_compra`

            WHERE
                factura_fecha >= '".$inicio."'
                and factura_fecha <= '".$fin."'
                and compra_id <> 0 
                
            ORDER BY `factura_id` ASC";
        
        $factura = $this->db->query($sql)->result_array();
      
        return $factura;
    }

