class Formula_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get formula by formula_id
     */
    function get_formula($formula_id)
    {
        
        $sql = "select f.*, p.* from formula f
                left join producto p on p.producto_id = f.producto_id
                where f.formula_id = ".$formula_id;
        
        $formula = $this->db->query($sql,array($formula_id))->row_array();

        return $formula;
    }
        
    /*
     * Get all formula
     */
    function get_all_formula()
    {
        $sql = "select * from formula f
                left join producto p on p.producto_id = f.producto_id
               ";
        
        $formula = $this->db->query($sql)->result_array();

        return $formula;
    }
        
    /*
     * function to add new formula
     */
    function add_formula($params)
    {
        $this->db->insert('formula',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update formula
     */
    function update_formula($formula_id,$params)
    {
        $this->db->where('formula_id',$formula_id);
        return $this->db->update('formula',$params);
    }
    
    /*
     * function to delete formula
     */
    function delete_formula($formula_id)
    {
        return $this->db->delete('formula',array('formula_id'=>$formula_id));
    }

    function consultar($sql)
    {
        return $this->db->query($sql)->result_array();
        
    }
    

    function ejecutar($sql)
    {
        $this->db->query($sql);
        return true;
    }

    function get_detalle_formula($formula_id)
    {
        $sql = "select * from detalle_formula f
                left join producto p on p.producto_id = f.producto_id  
                where f.formula_id = ".$formula_id;
        
        return $this->db->query($sql)->result_array();
    }
    
}
