class Gestion_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get gestion by cod_ges
     */
    function get_gestion($cod_ges)
    {
        $gestion = $this->db->query("
            SELECT
                *

            FROM
                `gestion`

            WHERE
                `cod_ges` = ?
        ",array($cod_ges))->row_array();

        return $gestion;
    }
        
    /*
     * Get all gestion
     */
    function get_all_gestion()
    {
        $gestion = $this->db->query("
            SELECT
                *

            FROM
                `gestion`

            WHERE
                1 = 1

            ORDER BY `cod_ges` DESC
        ")->result_array();

        return $gestion;
    }
        
    /*
     * function to add new gestion
     */
    function add_gestion($params)
    {
        $this->db->insert('gestion',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update gestion
     */
    function update_gestion($cod_ges,$params)
    {
        $this->db->where('cod_ges',$cod_ges);
        return $this->db->update('gestion',$params);
    }
    
    /*
     * function to delete gestion
     */
    function delete_gestion($cod_ges)
    {
        return $this->db->delete('gestion',array('cod_ges'=>$cod_ges));
    }
}
