class Idioma_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get idioma by idioma_id
     */
    function get_idioma($idioma_id)
    {
        $idioma = $this->db->query("
            SELECT
                *

            FROM
                `idioma`

            WHERE
                `idioma_id` = ?
        ",array($idioma_id))->row_array();

        return $idioma;
    }
    
    /*
     * Get all idioma count
     */
    function get_all_idioma_count()
    {
        $idioma = $this->db->query("
            SELECT
                count(*) as count

            FROM
                `idioma`
        ")->row_array();

        return $idioma['count'];
    }
        
    /*
     * Get all idioma
     */
    function get_all_idioma($params = array())
    {
        $limit_condition = "";
        if(isset($params) && !empty($params))
            $limit_condition = " LIMIT " . $params['offset'] . "," . $params['limit'];
        
        $idioma = $this->db->query("
            SELECT
                *

            FROM
                `idioma`

            WHERE
                1 = 1

            ORDER BY `idioma_id` DESC

            " . $limit_condition . "
        ")->result_array();

        return $idioma;
    }
        
    /*
     * function to add new idioma
     */
    function add_idioma($params)
    {
        $this->db->insert('idioma',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update idioma
     */
    function update_idioma($idioma_id,$params)
    {
        $this->db->where('idioma_id',$idioma_id);
        return $this->db->update('idioma',$params);
    }
    
    /*
     * function to delete idioma
     */
    function delete_idioma($idioma_id)
    {
        return $this->db->delete('idioma',array('idioma_id'=>$idioma_id));
    }
}
