class Imagen_producto_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get imagen by imagen_id
     */
    function get_imagen_producto($imagenprod_id)
    {
        $imagen = $this->db->query("
            SELECT
                *

            FROM
                `imagen_producto`

            WHERE
                `imagenprod_id` = ?
        ",array($imagenprod_id))->row_array();

        return $imagen;
    }
    
    /*
     * Get all imagen count
     */
    function get_all_imagen_producto_count()
    {
        $imagen = $this->db->query("
            SELECT
                count(*) as count

            FROM
                `imagen_producto`
        ")->row_array();

        return $imagen['count'];
    }
        
    /*
     * Get all imagen
     */
    function get_all_imagen_producto($params = array())
    {
        $limit_condition = "";
        if(isset($params) && !empty($params))
            $limit_condition = " LIMIT " . $params['offset'] . "," . $params['limit'];
        
        $imagen = $this->db->query("
            SELECT
                *

            FROM
                imagen_producto i, estado e

            WHERE
                i.estado_id = e.estado_id

            ORDER BY `imagenprod_id` DESC

            " . $limit_condition . "
        ")->result_array();

        return $imagen;
    }
        
    /*
     * function to add new imagen
     */
    function add_imagen_producto($params)
    {
        $this->db->insert('imagen_producto',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update imagen
     */
    function update_imagen_producto($imagenprod_id,$params)
    {
        $this->db->where('imagenprod_id',$imagenprod_id);
        return $this->db->update('imagen_producto',$params);
    }
    
    /*
     * function to delete imagen
     */
    function delete_imagen($imagenprod_id)
    {
        return $this->db->delete('imagen_producto',array('imagenprod_id'=>$imagenprod_id));
    }
    /*
     * Get all imagen count
     */
    function get_all_imagen_mi_producto_count($producto_id)
    {
        $imagen = $this->db->query("
            SELECT
                count(*) as count

            FROM
                imagen_producto i, estado e, producto p

            WHERE
                i.estado_id = e.estado_id
                and i.producto_id = p.producto_id
                and i.producto_id = '$producto_id'
        ")->row_array();

        return $imagen['count'];
    }
    /*
     * Get all imagen
     */
    function get_all_imagen_mi_producto($producto_id, $params = array())
    {
        $limit_condition = "";
        if(isset($params) && !empty($params))
            $limit_condition = " LIMIT " . $params['offset'] . "," . $params['limit'];
        
        $imagen = $this->db->query("
            SELECT
                i.*, e.estado_descripcion, e.estado_color

            FROM
                imagen_producto i, estado e, producto p

            WHERE
                i.estado_id = e.estado_id
                and i.producto_id = p.producto_id
                and i.producto_id = '$producto_id'

            ORDER BY `imagenprod_id` ASC
        ")->result_array();

        return $imagen;
    }
    /*
     * Get all imagen de DETALLES de SERVICIO
     */
    function get_all_imagen_mi_det($detalleserv_id)
    {
        $imagen = $this->db->query("
            SELECT
                i.*, e.estado_descripcion, e.estado_color
            FROM
                imagen_producto i
            LEFT JOIN estado e on i.estado_id = e.estado_id
            WHERE
                i.detalleserv_id = $detalleserv_id
            ORDER BY `imagenprod_id` DESC
        ")->result_array();

        return $imagen;
    }

    function get_all_imagen_mi_serv($servicio_id)
    {
        $sql = "select * 
                from imagen_producto i, detalle_serv d
                where 
                i.detalleserv_id =  d.detalleserv_id and
                d.servicio_id = ".$servicio_id;
        
        $imagen = $this->db->query($sql)->result_array();
        return $imagen;
    }
}
