class Inventario_usuario_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get inventario_usuario by inventario_id
     */
    function get_inventario_usuario($inventario_id)
    {
        return $this->db->get_where('inventario_usuario',array('inventario_id'=>$inventario_id))->row_array();
    }
        
    /*
     * Get all inventario_usuario
     */
    function get_all_inventario_usuario()
    {
       $invusuario = $this->db->query("
            SELECT
                iu.*, p.*, u.usuario_nombre

            FROM
                inventario_usuario iu
            LEFT JOIN producto p on p.producto_id=iu.producto_id
            LEFT JOIN usuario u on u.usuario_id=iu.usuario_id
               
            WHERE iu.inventario_fecha = CURDATE() 
            ORDER BY `inventario_id` DESC

            
        ")->result_array();

        return $invusuario;
    }

    function buscar_inventario_usuario($filtro)
    {
       $invusuario = $this->db->query("
            SELECT
                iu.*, p.*, u.usuario_nombre

            FROM
                inventario_usuario iu
            LEFT JOIN producto p on p.producto_id=iu.producto_id
            LEFT JOIN usuario u on u.usuario_id=iu.usuario_id
            
            WHERE 1=1
            ".$filtro."   

            ORDER BY `inventario_id` DESC

            
        ")->result_array();

        return $invusuario;
    }

    function get_producto($inventario_id)
    {
       $producto = $this->db->query("
            SELECT
                p.*, iu.producto_id

            FROM
               producto p, inventario_usuario iu
            where
                iu.inventario_id=".$inventario_id."
                and p.producto_id=iu.producto_id
               

            
        ")->row_array();

        return $producto;
    }
        
    /*
     * function to add new inventario_usuario
     */
    function add_inventario_usuario($params)
    {
        $this->db->insert('inventario_usuario',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update inventario_usuario
     */
    function update_inventario_usuario($inventario_id,$params)
    {
        $this->db->where('inventario_id',$inventario_id);
        return $this->db->update('inventario_usuario',$params);
    }
    
    /*
     * function to delete inventario_usuario
     */
    function delete_inventario_usuario($inventario_id)
    {
        return $this->db->delete('inventario_usuario',array('inventario_id'=>$inventario_id));
    }

    function actualizar_inventario($usuario_id, $fecha, $tipo)
    {
        $sql = "update inventario_usuario set inventario_ventas = 0, inventario_pedidos = 0, inventario_devoluciones = 0, inventario_saldo = inventario_cantidad 
                where usuario_id = ".$usuario_id." and inventario_fecha = '".$fecha."'";
                    
        $this->db->query($sql);
        
        
        if ($tipo==1){ //en funcion a las ventas
        
            $sql ="update inventario_usuario i
                    set i.inventario_ventas = 
                    (
                        select if(sum(d.detalleven_cantidad)>0,sum(d.detalleven_cantidad),0) as cantidad
                        from venta v, detalle_venta d
                        where 
                        d.producto_id = i.producto_id and
                        v.venta_fecha = '".$fecha."' and
                        v.venta_id = d.venta_id and
                        v.usuario_id = ".$usuario_id."
                        group by d.producto_id
                    )
                    ,i.inventario_saldo = if(i.inventario_cantidad - i.inventario_ventas>0,i.inventario_cantidad - i.inventario_ventas,i.inventario_saldo)
                    where 
                    i.usuario_id = ".$usuario_id." and
                    i.inventario_fecha = '".$fecha."'";
            
        }
        
        
        if ($tipo==2){//en funcion a las preventas
        
            $sql ="update inventario_usuario i
                    set i.inventario_ventas = 
                    (
                        select if(sum(d.detalleped_cantidad)>0,sum(d.detalleped_cantidad),0) as cantidad
                        from pedido v, detalle_pedido d
                        where 
                        d.producto_id = i.producto_id and
                        v.pedido_fecha = '".$fecha."' and
                        v.pedido_id = d.pedido_id and
                        v.usuario_id = ".$usuario_id."
                        group by d.producto_id
                    )
                    ,i.inventario_saldo = if(i.inventario_cantidad - i.inventario_ventas>0,i.inventario_cantidad - i.inventario_ventas,i.inventario_saldo)
                    where 
                    i.usuario_id = ".$usuario_id." and
                    i.inventario_fecha = '".$fecha."'";
            
        }
        
        
        if ($tipo==3){//en funcion a las entregas
        
            $sql ="update inventario_usuario i
                    set i.inventario_ventas = 
                    (
                        select if(sum(d.detalleven_cantidad)>0,sum(d.detalleven_cantidad),0) as cantidad
                        from venta v, detalle_venta d
                        where 
                        d.producto_id = i.producto_id and
                        v.venta_fecha = '".$fecha."' and
                        v.venta_id = d.venta_id and
                        v.entrega_usuarioid = ".$usuario_id." and
                        v.entrega_id = 2
                        group by d.producto_id
                    ),
                    i.inventario_ventas = if (i.inventario_ventas>0,i.inventario_ventas,0)
                    
                    where 
                    i.usuario_id = ".$usuario_id." and
                    i.inventario_fecha = '".$fecha."'";
            
        }
        //,i.inventario_saldo = if(i.inventario_cantidad - i.inventario_ventas>0,i.inventario_cantidad - i.inventario_ventas,i.inventario_saldo)
        $this->db->query($sql);
            
        $sql ="update inventario_usuario i set 
                    i.inventario_saldo = i.inventario_cantidad - i.inventario_ventas
                    where 
                    i.usuario_id = ".$usuario_id." and
                    i.inventario_fecha = '".$fecha."'";
        $this->db->query($sql);
        
        
        
        return true;
    }

    function eliminar_inventario($usuario_id, $fecha)
    {
        $sql = "delete from inventario_usuario
                where 
                usuario_id = ".$usuario_id." and
                inventario_fecha = '".$fecha."'";
                    
        $this->db->query($sql);
        
        return true;
    }
}
