 */
 
class Item_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get item by item_id
     */
    function get_item($item_id)
    {
        $item = $this->db->query("
            SELECT
                *

            FROM
                `item`

            WHERE
                `item_id` = ?
        ",array($item_id))->row_array();

        return $item;
    }
    
    /*
     * Get all item count
     */
    function get_all_item_count()
    {
        $item = $this->db->query("
            SELECT
                count(*) as count

            FROM
                `item`
        ")->row_array();

        return $item['count'];
    }
        
    /*
     * Get all item
     */
    function get_all_item($params = array())
    {
        $limit_condition = "";
        if(isset($params) && !empty($params))
            $limit_condition = " LIMIT " . $params['offset'] . "," . $params['limit'];
        
        $item = $this->db->query("
            SELECT
                *

            FROM
                item i, estado_pagina e, menu m, submenu s

            WHERE
                i.estadopag_id = e.estadopag_id
                and i.menu_id = m.menu_id
                and i.submenu_id = s.submenu_id

            ORDER BY `item_id` DESC

            " . $limit_condition . "
        ")->result_array();

        return $item;
    }
        
    /*
     * function to add new item
     */
    function add_item($params)
    {
        $this->db->insert('item',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update item
     */
    function update_item($item_id,$params)
    {
        $this->db->where('item_id',$item_id);
        return $this->db->update('item',$params);
    }
    
    /*
     * function to delete item
     */
    function delete_item($item_id)
    {
        return $this->db->delete('item',array('item_id'=>$item_id));
    }
}
