    /*
     * Get librocv by cod_lcv
     */
    function get_librocv($cod_lcv)
    {
        $librocv = $this->db->query("
            SELECT
                *

            FROM
                `librocv`

            WHERE
                `cod_lcv` = ?
        ",array($cod_lcv))->row_array();

        return $librocv;
    }
        
    /*
     * Get all librocv
     */
    function get_all_librocv()
    {
        $librocv = $this->db->query("
            SELECT
                *

            FROM
                `librocv`

            WHERE
                1 = 1

            ORDER BY `cod_lcv` DESC
        ")->result_array();

        return $librocv;
    }
        
    /*
     * function to add new librocv
     */
    function add_librocv($params)
    {
        $this->db->insert('librocv',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update librocv
     */
    function update_librocv($cod_lcv,$params)
    {
        $this->db->where('cod_lcv',$cod_lcv);
        return $this->db->update('librocv',$params);
    }
    
    /*
     * function to delete librocv
     */
    function delete_librocv($cod_lcv)
    {
        return $this->db->delete('librocv',array('cod_lcv'=>$cod_lcv));
