class Mapa_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get mapa by mapa_id
     */
    function get_mapa($mapa_id)
    {
        $mapa = $this->db->query("
            SELECT
                *

            FROM
                `mapa`

            WHERE
                `mapa_id` = ?
        ",array($mapa_id))->row_array();

        return $mapa;
    }
    
    /*
     * Get all mapa count
     */
    function get_all_mapa_count()
    {
        $mapa = $this->db->query("
            SELECT
                count(*) as count

            FROM
                `mapa`
        ")->row_array();

        return $mapa['count'];
    }
        
    /*
     * Get all mapa
     */
    function get_all_mapa($params = array())
    {
        $limit_condition = "";
        if(isset($params) && !empty($params))
            $limit_condition = " LIMIT " . $params['offset'] . "," . $params['limit'];
        
        $mapa = $this->db->query("
            SELECT
                *

            FROM
                mapa m, estado_pagina e, pagina_web p

            WHERE
                m.estadopag_id = e.estadopag_id
                and m.pagina_id = p.pagina_id

            ORDER BY `mapa_id` DESC

            " . $limit_condition . "
        ")->result_array();

        return $mapa;
    }
        
    /*
     * function to add new mapa
     */
    function add_mapa($params)
    {
        $this->db->insert('mapa',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update mapa
     */
    function update_mapa($mapa_id,$params)
    {
        $this->db->where('mapa_id',$mapa_id);
        return $this->db->update('mapa',$params);
    }
    
    /*
     * function to delete mapa
     */
    function delete_mapa($mapa_id)
    {
        return $this->db->delete('mapa',array('mapa_id'=>$mapa_id));
    }
}
