    /*
     * Get modelo_contrato de una beca
     */
    function get_modelocontrato_beca($beca_id)
    {
        return $this->db->get_where('modelo_contrato',array('beca_id'=>$beca_id))->row_array();
    }
    /**
     * Obtener datos del para el contrato
     */
    function get_datosContrato($venta_id){
        return $this->db->query(
            "SELECT v.venta_total,v.venta_fecha, v.venta_id 
                , c.credito_cuotainicial, c.credito_monto, c.credito_numpagos, c.credito_fechalimite 
                ,c2.cuota_total
                ,p.*
                ,m.moneda_descripcion 
                ,max(c2.cuota_numcuota) as cuotas 
                ,dv.detalleven_id
                ,cl.cliente_id, cl.cliente_nombre, cl.cliente_ci, cl.cliente_nit 
                ,cp.categoria_nombre
                ,sp.subcategoria_nombre
                ,u.usuario_nombre, u.usuario_ci
            from venta v 
            left join detalle_venta dv on v.venta_id = dv.venta_id  
            left join moneda m on m.moneda_id = v.moneda_id 
            left join producto p on p.producto_id = dv.producto_id 
            left join categoria_producto cp on cp.categoria_id = p.categoria_id 
            left join subcategoria_producto sp on sp.subcategoria_id = p.subcategoria_id 
            left join credito c on c.venta_id = v.venta_id 
            left join cuota c2 on c2.credito_id = c.credito_id 
            left join cliente cl on cl.cliente_id = v.cliente_id 
            left join usuario u on u.usuario_id = v.usuario_id
            where v.venta_id = $venta_id"
        )->row_array();
    }
}
