                left join usuario u on o.usuario_id1=u.usuario_id
                left join usuario y on o.usuario_id2=y.usuario_id
                where e.estado_id = 9  and o.orden_fechapago = date(now()) and ".$filtro." ";
        $resultado = $this->db->query($sql)->result_array();
        return $resultado;
    }
        
