class Preferencia_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get preferencia by preferencia_id
     */
    function get_preferencia($preferencia_id)
    {
        $preferencia = $this->db->query("
            SELECT
                *

            FROM
                `preferencia`

            WHERE
                `preferencia_id` = ?
        ",array($preferencia_id))->row_array();

        return $preferencia;
    }
        
    /*
     * Get all preferencia
     */
    function get_all_preferencia()
    {
        $preferencia = $this->db->query("
            SELECT
                *

            FROM
                `preferencia`

            WHERE
                1 = 1

            ORDER BY `preferencia_id` DESC
        ")->result_array();

        return $preferencia;
    }
                
    /*
     * Get all preferencia
     */
    function get_producto_preferencia()
    {
//        $sql= "select r.producto_id,p.* from producto_preferencia r, preferencia p
//               where r.preferencia_id = p.preferencia_id
//               ";
        $sql= "select * from conspreferencia";
        return $this->db->query($sql)->result_array();
        
    }
        
    /*
     * function to add new preferencia
     */
    function add_preferencia($params)
    {
        $this->db->insert('preferencia',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update preferencia
     */
    function update_preferencia($preferencia_id,$params)
    {
        $this->db->where('preferencia_id',$preferencia_id);
        return $this->db->update('preferencia',$params);
    }
    
    /*
     * function to delete preferencia
     */
    function delete_preferencia($preferencia_id)
    {
        return $this->db->delete('preferencia',array('preferencia_id'=>$preferencia_id));
    }
    /*
     * Get all preferencia count
     */
    function get_all_preferencia_count()
    {
        $preferencia = $this->db->query("
            SELECT
                count(*) as count
            FROM
                `preferencia`
        ")->row_array();

        return $preferencia['count'];
    }
    /*
     * Get all preferencia
     */
    function get_preferencia_all($params = array())
    {
        $limit_condition = "";
        if(isset($params) && !empty($params))
            $limit_condition = " LIMIT " . $params['offset'] . "," . $params['limit'];
        
        $preferencia = $this->db->query("
            SELECT
                p.*, e.estado_descripcion, e.estado_color
            FROM
                `preferencia` p, estado e
            WHERE
                p.estado_id = e.estado_id
            ORDER BY `preferencia_descripcion`
            " . $limit_condition . "
        ")->result_array();

        return $preferencia;
    }
    /* obtiene todas las preferencias segun el estado_id */
    function get_all_preferenciaestado($estado_id)
    {
        $preferencia = $this->db->query("
            SELECT
                p.*
            FROM
                `preferencia` p
            WHERE
                p.estado_id = $estado_id

            ORDER BY `p`.`preferencia_descripcion`
        ")->result_array();

        return $preferencia;
    }
}
