class Procedencia_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get procedencia by procedencia_id
     */
    function get_procedencia($procedencia_id)
    {
        return $this->db->get_where('procedencia',array('procedencia_id'=>$procedencia_id))->row_array();
    }
        
    /*
     * Get all procedencia
     */
    function get_all_procedencia()
    {
        $procedencia = $this->db->query("
            SELECT
                *

            FROM
                procedencia p, estado e

            WHERE
                p.estado_id = e.estado_id

        ")->result_array();

        return $procedencia;
    }
        
    /*
     * function to add new procedencia
     */
    function add_procedencia($params)
    {
        $this->db->insert('procedencia',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update tipo_servicio
     */
    function update_procedencia($procedencia_id,$params)
    {
        $this->db->where('procedencia_id',$procedencia_id);
        return $this->db->update('procedencia',$params);
    }
    
    /*
     * function to delete procedencia
     */
    function delete_procedencia($procedencia_id)
    {
        return $this->db->delete('procedencia',array('procedencia_id'=>$procedencia_id));
    }
    
    /*
     * Get all procedencia con id ==1
     */
    function get_all_procedencia_id1()
    {
        $procedencia = $this->db->query("
            SELECT
                p.`procedencia_id`, p.`procedencia_descripcion`

            FROM
                procedencia p, estado e

            WHERE
                p.estado_id = e.estado_id
                and e.`estado_tipo` = 1
                and e.estado_id = 1
                
        ")->result_array();

        return $procedencia;
    }
}
