class Producto_precios_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get unidad by unidad_id
     */
    function get_productos($unidad_id)
    {
        return $this->db->get_where('unidad',array('unidad_id'=>$unidad_id))->row_array();
    }
        
    /*
     * Get all unidad
     */
    function get_all_productos()
    {
        $sql = "select * from producto_precios";        
        return $this->db->query($sql)->result_array();

    }

    function cargar_producto_precios()
    {
        // Consulta SQL para copiar datos de 'producto' a 'producto_precios'
        $sql = "INSERT INTO producto_precios (producto_id, producto_nombre, producto_costo, producto_precio, producto_tipocambio, producto_preciofactor, producto_preciofactor1, producto_preciofactor2, producto_preciofactor3, producto_preciofactor4, producto_ultimocosto, producto_costoenvase, producto_precioenvase)
                SELECT producto_id, producto_nombre, producto_costo, producto_precio, producto_tipocambio, producto_preciofactor, producto_preciofactor1, producto_preciofactor2, producto_preciofactor3, producto_preciofactor4, producto_ultimocosto, producto_costoenvase, producto_precioenvase
                FROM producto";
        
        // Ejecutar la consulta
        $this->db->query($sql);
    }
    
    /*
     * function to add new unidad
     */
    function add_productos($params)
    {
        $this->db->insert('unidad',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update unidad
     */
    function update_productos($unidad_id,$params)
    {
        $this->db->where('unidad_id',$unidad_id);
        return $this->db->update('unidad',$params);
    }
    
    /*
     * function to delete unidad
     */
    function delete_productos($unidad_id)
    {
        return $this->db->delete('unidad',array('unidad_id'=>$unidad_id));
    }
    /*
     * Get all unidad
     */
    function get_productos_usada($nombre_productos)
    {
        $unidad = $this->db->query("
            SELECT
                u.*
            FROM
                unidad u
            left join producto p on u.unidad_nombre = p.producto_productos
            where
                p.producto_productos = '".$nombre_productos."'

        ")->result_array();

        return $unidad;
    }

    function truncate_table(){
        $this->db->query("TRUNCATE unidad");
    }
}
