class Proveedor_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get proveedor by proveedor_id
     */
    function get_proveedor($proveedor_id)
    {
        $proveedor = $this->db->query("
            SELECT
                *

            FROM
                `proveedor`

            WHERE
                `proveedor_id` = ?
        ",array($proveedor_id))->row_array();

        return $proveedor;
    }
    
    /*
     * Get all proveedor count
     */
    function get_all_proveedor_count()
    {
        $proveedor = $this->db->query("
            SELECT
                count(*) as count

            FROM
                `proveedor`
        ")->row_array();

        return $proveedor['count'];
    }
        
    /*
     * Get all proveedor
     */
    function get_all_proveedor($params = array())
    {
        $limit_condition = "";
        if(isset($params) && !empty($params))
            $limit_condition = " LIMIT " . $params['offset'] . "," . $params['limit'];
        
        $proveedor = $this->db->query("
            SELECT
                p.*, e.*

            FROM
                proveedor p, estado e

            WHERE
                p.estado_id=e.estado_id

            ORDER BY `proveedor_id` DESC

            " . $limit_condition . "
        ")->result_array();

        return $proveedor;
    }
        
    /*
     * function to add new proveedor
     */
    function add_proveedor($params)
    {
        $this->db->insert('proveedor',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update proveedor
     */
    function update_proveedor($proveedor_id,$params)
    {
        $this->db->where('proveedor_id',$proveedor_id);
        return $this->db->update('proveedor',$params);
    }
    
    /*
     * function to delete proveedor
     */
    function delete_proveedor($proveedor_id)
    {
        return $this->db->delete('proveedor',array('proveedor_id'=>$proveedor_id));
    }
    /* **********busca proveedores************* */
    function get_busqueda_proveedor($filtr)
    {
        $filtro = str_replace("%20", " ", $filtr);
        $proveedor = $this->db->query("
            SELECT
                p.*, e.*

            FROM
                proveedor p, estado e

            WHERE
                p.estado_id=e.estado_id
                and (p.proveedor_nombre like '%".$filtro."%' or p.proveedor_codigo like '%".$filtro."%' 
                    or p.proveedor_contacto like '%".$filtro."%' or p.proveedor_nit like '%".$filtro."%')

            ORDER BY `proveedor_id` DESC

        ")->result_array();

        return $proveedor;
    }

    /*add by Fito*/
    public function get_proveedores()
    {
        $this->db->select('*');
        $this->db->from('proveedor');
        $this->db->order_by("proveedor_nombre", "asc");
        $query = $this->db->get();
        return $query->result();
    }
    /*
     * Get all proveedor activos
     */
    function get_all_proveedor_activo()
    {
        $proveedor = $this->db->query("
            SELECT
                p.*, e.*
            FROM
                proveedor p, estado e
            WHERE
                p.estado_id=e.estado_id
                and p.estado_id =1
            ORDER BY `proveedor_nombre` ASC
        ")->result_array();
        return $proveedor;
    }

}
